import React, { Component } from "react";
import axios from "axios";
import Note from "./Note";
import { Link } from "react-router-dom";

const Exercise = props => (
    <tr>
        <td>{props.exercise.username}</td>
        <td>{props.exercise.description}</td>
        <td>{props.exercise.duration}</td>
        <td>{props.exercise.date.substring(0, 10)}</td>
        <td>
            <Link to={"/edit/" + props.exercise._id}>edit</Link> | <a href="#" onClick={() => { props.deleteExercise(props.exercise._id) }}>delete</a>
        </td>
    </tr>
)

export default class NoteList extends Component {
    constructor(props) {
        super(props);
        //not sure what bind means. Also I already have deleteNote function in App.jsx
        this.deleteNote = this.deleteNote.bind(this);

        this.state = {
            notes: []
        };
    }

    componentDidMount() {
        axios.get('http://localhost:5000/')
            .then(response => {
                this.setState({ notes: response.data })
            })
            .catch((error) => {
                console.log(error);
            })
    }

    deleteNote(id) {
        console.debug('id:', id)
        //delete note with id from database
        axios.delete('http://localhost:5000/' + id)
            .then(response => {
                console.log(response.data)
            });
        //set new state
        this.setState({
            notes: this.state.notes.filter(singleNote => {
                return singleNote._id !== id
            })
        })
    }

    noteList() {
        return this.state.notes.map(singleNote => {
            return <Note
                key={singleNote._id}
                title={singleNote.title}
                content={singleNote.content}
                onDelete={this.deleteNote}
            />
        })
    }

    render() {
        return (
            <div>
                {this.noteList()}
            </div>
        )
    }
}
