import React, { useState } from "react";

function InputArea(props) {

    const[note, setNote] =useState({
        title:"",
        content:""
    });

    function handleChange(event) {
        const {name, value} = event.target;

        setNote(newValue);
    }

    return (
            <form action="">
             <input
            type="text" 
            name="title"
            onChange={handleChange} 
            value={note.title}
            placeholder="Title"
            />
            <textarea 
            name="content"
            onChange={handleChange}
            value={note.content}
            id="" 
            cols="30" 
            rows="10"
            placeholder="Take a note"
            >

            </textarea>
            <button
            onClick={() => {
                props.onAdd(note);
                setNote("");
            }}
            >Add</button>
            </form>



    );
}

export default InputArea;
